-- Initial migration: create a simple table to record analyses

CREATE TABLE IF NOT EXISTS analyses (
    id SERIAL PRIMARY KEY,
    name VARCHAR NOT NULL,
    birth_date DATE NOT NULL,
    birth_hour INT NOT NULL,
    result_birth JSONB NOT NULL,
    result_name JSONB NOT NULL,
    summary TEXT NULL,
    detail TEXT NULL,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT now()
);

-- Create kanji table for stroke counts (imported from ucs-strokes)
CREATE TABLE IF NOT EXISTS kanji (
    char TEXT PRIMARY KEY,
    codepoint TEXT NOT NULL,
    strokes_text TEXT,
    strokes_min INTEGER,
    strokes_max INTEGER,
    source TEXT
);
CREATE INDEX IF NOT EXISTS idx_kanji_codepoint ON kanji(codepoint);


CREATE TABLE IF NOT EXISTS llm_responses (
    id SERIAL PRIMARY KEY,
    request_id TEXT,
    provider TEXT,
    model TEXT,
    model_version TEXT,
    response_id TEXT,
    prompt_hash TEXT,
    response_text TEXT,
    usage JSONB,
    raw JSONB,
    created_at TIMESTAMPTZ DEFAULT now()
);

